def process_resume(resume):
    processed_token = []
    for token in resume.split():
        token = ''.join(e.lower() for e in token if e.isalnum())
        processed_token.append(token)
    return ' '.join(processed_token)

def print_doc_entities(_doc: Doc):
    if _doc.ents:
        for _ent in _doc.ents:
            print(f" {_ent.text} {_ent.label_}")
        else:
            print(" NONE")

def get_train_data():
    patternFile = "data/resume-dataset/Resume/jz_skill_patterns.jsonl"

    resumeData = pd.read_csv('data/resume-dataset/Resume/Resume.csv')
    resumeData = resumeData.loc[resumeData['Category']=='INFORMATION-TECHNOLOGY'].dropna()

    nlp = spacy.load('en_core_web_lg')
    ruler = nlp.add_pipe("entity_ruler")

    ruler.from_disk(patternFile)
    trainingData = []
    outfile = open("text", "w")
    for i in range(len(resumeData['Resume_str'])):
        resume = process_resume(resumeData.iloc[i]['Resume_str']).strip()
        doc = nlp(resume)
        entityData = []
        for ent in doc.ents:
            print(ent, ent.start_char, ent.end_char)
            if ent.label_ == "SKILL":
                entityData.append((ent.start_char, ent.end_char, ent.label_,))
            #print([(ent.text, ent.label_, ent.start, ent.end) for ent in doc.ents])
            # print((resume, entityData))
        writeStr = str((resume, entityData)) + "\n"
        outfile.write(writeStr)
        trainingData.append((resume, entityData))

    return trainingData

